import Header from './components/Navbar';
import Nvidia from './components/Nvidia';
import Radeon from './components/Nvidia';
import Cart from './components/Cart';
import data from './data';
import { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

function App() {
  const { products } = data;
  const [cartItems, setCartItems] = useState([]);
  const onAdd = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }]);
    }
  };
  const onRemove = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== product.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };
  return (
    <div className="App">
      <Router>
          <Header countCartItems={cartItems.length}></Header>
      </Router>
      <div className="row">
        <Nvidia products={products} onAdd={onAdd}></Nvidia>
        <Cart cartItems={cartItems} onAdd={onAdd} onRemove={onRemove}></Cart>

      </div>
      <div className="row">
        <Radeon products={products} onAdd={onAdd}></Radeon>


      </div>
    </div>
  );
}

export default App;
